// 1. Inject CSS as a <style> block
const style = document.createElement("style");
style.innerText = `
  .statblock-table-header {
    font-weight: var(--active-table-header-font-weight);
  }
  .statblock-table {
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    flex-wrap: wrap;
  }
  .table-item {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-flow: column nowrap;
  }
`;
document.head.appendChild(style);

// 2. Data
const headers = ["Difficulty", "Thresholds", "HP", "Stress", "ATK"];
const values = [
  [monster.difficulty],
  [monster.thresholds],
  [monster.hp],
  [monster.stress],
  [monster.atk]
];

// 3. Create stat block container
const statBlock = document.createElement("div");
statBlock.classList.add("statblock-table");

// 4. Generate stat columns
for (let i = 0; i < headers.length; i++) {
  const tableItem = document.createElement("div");
  tableItem.classList.add("table-item");

  const header = document.createElement("span");
  header.classList.add("statblock-table-header");
  header.innerText = headers[i];
  tableItem.appendChild(header);

  for (let val of values[i]) {
    const valueSpan = document.createElement("span");
    valueSpan.innerText = val;
    tableItem.appendChild(valueSpan);
  }

  statBlock.appendChild(tableItem);
}

// 5. Append to the DOM
return statBlock; // or use a specific container
